"""A module that swaps two tokens on Uniswap pool."""

import json
from web3 import Web3

# Setting up Etherium endpoint for web3
infura_url = 'https://ropsten.infura.io/v3/4c3ca906f2c24666adc9f8e757b4d928'
web3 = Web3(Web3.HTTPProvider(infura_url))

# Tokens to swap
tokenIn = '0x6cF1Af1CD82D19EAEBf7d43cC32d46067bA0C96B'  # FGT
tokenOut = '0x1555a66Eb503D91a9d32042E282af5dF37271977'  # MYT

# Wallet address
recipient =	'0x5C036bEe95B37532D7bc364f756efa10667f1aA3'
# Key for signing
recipient_private_key = "31921fc8e1998857375c3f06fda830226d0025053b4a76294a8e77df0c1ad6fe"

# Swaping parameters
amountIn = 1000000000000000         # Amount of token 1 to swap
deadline = 16305283860              # Time limit for transaction
amountOutMinimum = 994537129224845  # Minimal amount of token 2 to return
sqrtPriceLimitX96 = 0               # Price Limit
fee = 500                           # 0.5%

# Transaction parameters
nonce = web3.eth.getTransactionCount(recipient)
tx = {
    'gas': 700000,
    'gasPrice': web3.toWei('50', 'gwei'),
    'from': recipient,
    'nonce': nonce
}



# Uniswap SwapRouter V3 Contract Web3 object
